------------------------------------------------------------------------------------------------------
--Dropping the tables if they already exist in the database
------------------------------------------------------------------------------------------------------
DROP TABLE IF EXISTS Anime CASCADE;
DROP TABLE IF EXISTS StatusTable CASCADE;
DROP TABLE IF EXISTS UserAnime CASCADE;
DROP TABLE IF EXISTS AnimeRecommendations CASCADE;
DROP TABLE IF EXISTS UserAnimeRatings CASCADE;

------------------------------------------------------------------------------------------------------
--Creating Anime table
------------------------------------------------------------------------------------------------------
CREATE TABLE Anime (
    AnimePlanetID INT PRIMARY KEY,
    Name VARCHAR(255) NOT NULL,
    AlternativeName VARCHAR(255),
    RatingScore DECIMAL(3, 2),
    NumberVotes INT,
    Tags VARCHAR(255),
    ContentWarning VARCHAR(255),
    Type VARCHAR(50),
    Episodes INT,
    Finished BOOLEAN,
    Duration VARCHAR(20),
    StartYear INT,
    EndYear INT,
    Season VARCHAR(20),
    Studios VARCHAR(255),
    Url VARCHAR(255)
);

------------------------------------------------------------------------------------------------------
--Creating StatusTable table
------------------------------------------------------------------------------------------------------
CREATE TABLE StatusTable (
    status INT PRIMARY KEY,
    description TEXT
);

------------------------------------------------------------------------------------------------------
--Creating UserAnime table 
------------------------------------------------------------------------------------------------------
CREATE TABLE UserAnime (
    user_id INT,
    anime_id INT,
    rating INT,
    watching_status INT,
    watched_episodes INT,
    PRIMARY KEY (user_id, anime_id),
    FOREIGN KEY (anime_id) REFERENCES Anime(AnimePlanetID),
    FOREIGN KEY (watching_status) REFERENCES StatusTable(status)
);

------------------------------------------------------------------------------------------------------
--Creating AnimeRecommendations table
------------------------------------------------------------------------------------------------------
CREATE TABLE AnimeRecommendations (
    AnimeID INT,
    RecommendationID INT,
    AgreeVotes INT,
    PRIMARY KEY (AnimeID, RecommendationID),
    FOREIGN KEY (AnimeID) REFERENCES Anime(AnimePlanetID)
);

------------------------------------------------------------------------------------------------------
--Creating UserAnimeRatings table 
------------------------------------------------------------------------------------------------------
CREATE TABLE UserAnimeRatings (
    user_id INT,
    anime_id INT,
    rating INT,
    PRIMARY KEY (user_id, anime_id),
    FOREIGN KEY (anime_id) REFERENCES Anime(AnimePlanetID)
);

